content.value:
:root {
  --btnchoicelight: #ff4242;
  --btnchoicedark: #b82e2e;
  --btninputlight: #92DE34;
  --btninputdark: #69BC22;
  --btninventorylight: #58a0e8;
  --btninventorydark: #3b6ea1;
  --btnchoicetext: #000000;
  --backgroundcolor: #FFFFFF;
}

body{
    background-repeat: no-repeat;
    background-size: auto 100vh;
    background-position: top;
    background-color: var(--backgroundcolor);
    -webkit-transition: background-image 0.2s ease;
    transition: background-image 0.2s ease;
    overflow: hidden;
}

#bottom {
    margin-bottom: 5px;
    margin-right: 2%;
    margin-left: 1%;
    position: fixed;
    bottom:0;
    width:97%;
}

#bloc-text{
    padding-top: 5px;
    border-radius: 20px;
    background-color: black;
    opacity:0.75;
    height: 20vh;
    width:100%;
}

.unselectable {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

#personnage{
    color:red;
    text-indent: 75px;
    font-size:22px;
    margin-bottom:-16px;
    margin-top: 2px;
}

#text {
    text-indent: 30px;
    margin-left: 20px;
    margin-right: 20px;
    color: white;
    text-align: justify;
    font-size:18px;
}

.fade-enter-active,
.fade-leave-active {
    transition: opacity 0.5s ease;
}

.fade-enter-from,
.fade-leave-to {
    opacity: 0;
}

#inputLoc{
    grid-column: 2;
    grid-row: 1;
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-gap: 10px;
}

#lineInput{
    display: grid;
    grid-template-columns: repeat(3, 1fr);
}

#buttoninput{
    grid-column: 4;
    grid-row: 1;
    height: 70%;
    margin-top: 10%;
    background-color: var(--btninputlight);
    box-shadow: 0 0 0 7px var(--btninputlight) inset;
    color: var(--btninputtext);
}

#buttoninput:hover {
  background-image: linear-gradient(
    145deg,
    transparent 10%,
    var(--btninputdark) 10% 20%,
    transparent 20% 30%,
    var(--btninputdark) 30% 40%,
    transparent 40% 50%,
    var(--btninputdark) 50% 60%,
    transparent 60% 70%,
    var(--btninputdark) 70% 80%,
    transparent 80% 90%,
    var(--btninputdark) 90% 100%
  );
  animation: background 3s linear infinite;
}

.buttonClass{
    display: block;
    cursor: pointer;
    outline: none;
    height: 35px;
    border: none;
    border-radius: 30px;
    font-weight: 600;
    background-size: 100% 100%;
}

.choiceButton{
    background-color: var(--btnchoicelight);
    box-shadow: 0 0 0 7px var(--btnchoicelight) inset;
    color: var(--btnchoicetext);
}

.choiceButton:hover {
  background-image: linear-gradient(
    145deg,
    transparent 10%,
    var(--btnchoicedark) 10% 20%,
    transparent 20% 30%,
    var(--btnchoicedark) 30% 40%,
    transparent 40% 50%,
    var(--btnchoicedark) 50% 60%,
    transparent 60% 70%,
    var(--btnchoicedark) 70% 80%,
    transparent 80% 90%,
    var(--btnchoicedark) 90% 100%
  );
  animation: background 3s linear infinite;
}

@keyframes background {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 400px 0;
  }
}

#input {
    grid-column: 1/4;
    grid-row: 1;
    width: 100%;
    padding: 12px 20px;
    margin: 8px 0;
    box-sizing: border-box;
    outline: none;
}

#choicesLoc{
    display: grid;
    grid-gap: 10px;
    margin: 20px 5%;
}

#grid2choices{
    grid-template-columns: repeat(2, 1fr);
}

#grid3choices{
    grid-template-columns: repeat(3, 1fr);
}

#grid4choices{
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(2, 1fr);
}

#grid5choices{
    grid-template-columns: repeat(6, 1fr);
    grid-template-rows: repeat(2, 1fr);
}

#grid6choices{
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(2, 1fr);
}

#grid7choices{
    grid-template-columns: repeat(8, 1fr);
    grid-template-rows: repeat(2, 1fr);
}

#grid8choices{
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: repeat(2, 1fr);
}

#pagesinventorypanel{
    display: grid;
    grid-gap: 10px;
    grid-template-columns: repeat(10, 1fr);
    margin: 20px 5%;
}

#leftpagebutton{
    grid-column: 1;
    grid-row: 1;
    width: 100%;
    background-color: var(--btninventorylight);
    box-shadow: 0 0 0 7px var(--btninventorylight) inset;
    color: var(--btnchoicetext);
}

#rightpagebutton{
    grid-column: 10;
    grid-row: 1;
    width: 100%;
    background-color: var(--btninventorylight);
    box-shadow: 0 0 0 7px var(--btninventorylight) inset;
    color: var(--btnchoicetext);
}

.inventorybutton{
    grid-column: 2/10;
    grid-row: 1;
    width: 100%;
    background-color: var(--btninventorylight);
    box-shadow: 0 0 0 7px var(--btninventorylight) inset;
    color: var(--btnchoicetext);
}

.buttonInventory:hover {
  background-image: linear-gradient(
    145deg,
    transparent 10%,
    var(--btninventorydark) 10% 20%,
    transparent 20% 30%,
    var(--btninventorydark) 30% 40%,
    transparent 40% 50%,
    var(--btninventorydark) 50% 60%,
    transparent 60% 70%,
    var(--btninventorydark) 70% 80%,
    transparent 80% 90%,
    var(--btninventorydark) 90% 100%
  );
  animation: background 3s linear infinite;
}

.modal-mask {
    position: fixed;
    z-index: 9998;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    display: table;
}

.modal-wrapper {
    display: table-cell;
    vertical-align: middle;
}

.modal-container {
    position: relative;
    width: 70%;
    height: 70vh;
    margin: 0px auto;
    padding: 20px 30px;
    background-color: #fff;
    border-radius: 2px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.33);
    font-family: Helvetica, Arial, sans-serif;
}

.modal-header h3 {
    margin-top: 0;
    color: #42b983;
}

.modal-body {
    height: 85%;
    overflow: auto;
    display: grid;
    grid-gap: 10px;
    grid-template-columns: repeat(8, 1fr);
    grid-auto-rows: 30%;
}

.modal-footer {
    bottom: 0;
    margin-bottom: 20px;
    position: absolute;
    margin-left: -50px;
    width: 100%;
}

.modal-default-button {
    float: right;
    width: 20%;
}

.modal-fade-enter,
.modal-fade-leave-to {
    opacity: 0;
}

.modal-fade-enter-active,
.modal-fade-leave-active {
    transition: opacity .5s ease;
}

#titleInventory{
    color : var(--btninventorydark);
}

.buttonitem{
    height: 100%;
    width: 100%;
    display: grid;
    grid-template-rows: repeat(5, 1fr);
    background-color: var(--btninventorylight);
    box-shadow: 0 0 0 7px var(--btninventorylight) inset;
    color: var(--btnchoicetext);
}

.buttonitem:hover {
  background-image: linear-gradient(
    145deg,
    transparent 10%,
    var(--btninventorydark) 10% 20%,
    transparent 20% 30%,
    var(--btninventorydark) 30% 40%,
    transparent 40% 50%,
    var(--btninventorydark) 50% 60%,
    transparent 60% 70%,
    var(--btninventorydark) 70% 80%,
    transparent 80% 90%,
    var(--btninventorydark) 90% 100%
  );
  animation: background 3s linear infinite;
}

.imgitem{
    width: 100%;
    margin-top: 15%;
    height: 90%;
    grid-row: 1/5;
    pointer-events: none;
}

.textitem{
    grid-row: 5;
    pointer-events: none;
}

#characterDiv {
    width: 95%;
    height: 100%;
    position: fixed;
    bottom: 0;
    display: grid;
    grid-gap: 1%;
    justify-items: center;

}

.imgcharacter{
    height: 100%;
    pointer-events: none;
    min-width: 0;
    width: auto;
    align-self: center;
    justify-self: center;
}

function getDialogue(page, index, assets){
    var pageA = assets[6].content.filter((p) => p.title === page);
    if(pageA.length === 0) return null;
    return pageA[0].listDialogues[index];
}

function getTransitionDialogue(page, id, assets){
    var pageA = assets[6].content.filter((p) => p.title === page);
    if(pageA.length === 0) return null;
    for(var i = 0; i<pageA[0].listDialogues.length; i++){
        if(pageA[0].listDialogues[i].id === id) return {d : pageA[0].listDialogues[i], index : i};
    }
    return null;
}

function getCharacter(name, assets){
    return getAssetIndex(0, name, assets);
}

function getScene(name, assets){
    return getAssetIndex(1, name, assets);
}

function getObject(name, assets){
    return getAssetIndex(2, name, assets);
}

function getMusic(name, assets){
    return getAssetIndex(3, name, assets);
}

function getSound(name, assets){
    return getAssetIndex(4, name, assets);
}

function setValueVariableByName(name, value, assets){
    var varA = assets[5].content.filter((p) => p.name === name);
    if(varA.length === 0) return null;
    var variable = varA[0];
    variable.value = {value : value, type : "value"};
}

function getVariableValueByName(name, assets){
    var varA = assets[5].content.filter((p) => p.name === name);
    if(varA.length === 0) return null;
    var variable = varA[0];
    return getVariableValue(variable, assets);
}

function getVariableValue(variable, assets){
    if(variable.value.type === "value" || variable.type.name === "Variable"){
        if(variable.type.name === "Boolean" && (variable.value.operation !== undefined && variable.value.operation !== "value")){
            switch (variable.value.operation) {
                case "==":
                    return getVariableValue(variable.value.input1, assets) === getVariableValue(variable.value.input2, assets);
                case ">":
                    return getVariableValue(variable.value.input1, assets) > getVariableValue(variable.value.input2, assets);
                case ">=":
                    return getVariableValue(variable.value.input1, assets) >= getVariableValue(variable.value.input2, assets);
                case "<":
                    return getVariableValue(variable.value.input1, assets) < getVariableValue(variable.value.input2, assets);
                case "<=":
                    return getVariableValue(variable.value.input1, assets) <= getVariableValue(variable.value.input2, assets);
            }
            return false;
        } else if((variable.type.name === "Integer" || variable.type.name === "Float") && (variable.value.operation !== undefined && variable.value.operation !== "value")){
            let input1 = (variable.type.name === "Integer" ? Math.trunc(getVariableValue(variable.value.input1, assets)) : getVariableValue(variable.value.input1, assets));
            let input2 = (variable.type.name === "Integer" ? Math.trunc(getVariableValue(variable.value.input2, assets)) : getVariableValue(variable.value.input2, assets));
            switch (variable.value.operation) {
                case "+":
                    return input1 + input2;
                case "-":
                    return input1 - input2;
                case "x":
                    return input1 * input2;
                case "รท":
                    return (variable.type.name === "Integer" ? Math.trunc(input1 / input2) : input1 / input2);
            }
            return false;
        }
        return variable.value.value;
    }
    if(variable.value.type === "variable"){
        if(variable.type.name === "Integer") return Math.trunc(getVariableValueByName(variable.value.value, assets));
        return getVariableValueByName(variable.value.value, assets);
    }
}

function getAssetIndex(index, name, assets){
    var asset = assets[index].content.filter((c) => c.name === name);
    if(asset.length === 0) return null;
    return asset[0];
}
